import React from 'react';
import { Link } from 'react-router-dom';
import { Container, Row, Col } from 'react-bootstrap';
import '../scss/footer.scss';

import logo from '../assets/logo.svg';
import facebook from '../../assets/facebook.svg';
import twitter from '../../assets/twitter.svg';
import instagram from '../../assets/instagram.svg';
import upchevron from '../../assets/up-chevron.svg';

export default function Footer() {
  const scrollTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const viewportContext = React.createContext({});

  const ViewportProvider = ({ children }) => {
    const [width, setWidth] = React.useState(window.innerWidth);
    const [height, setHeight] = React.useState(window.innerHeight);

    const handleWindowResize = () => {
      setWidth(window.innerWidth);
      setHeight(window.innerHeight);
    }

    React.useEffect(() => {
      window.addEventListener("resize", handleWindowResize);
      return () => window.removeEventListener("resize", handleWindowResize);
    }, []);

    return (
      <viewportContext.Provider value={{ width, height }}>
        {children}
      </viewportContext.Provider>
    );
  };

  const useViewport = () => {
    const { width, height } = React.useContext(viewportContext);
    return { width, height };
  }

  return (
    <>
      <footer className="footer mt-auto py-3">
        <Container>
          <Row className="py-5">
            <Col lg xs={{ span: 12 }}>
              <img src={logo} alt="store logo" className="mb-3" />
              <p>GAME NATION</p>
            </Col>
            <Col lg xs={{ span: 12 }}>
              <p className="footer-title">Support</p>
              <Link to="/" className="footer-link">
                <p>Contact Us</p>
              </Link>
              <Link to="/" className="footer-link">
                <p>FAQ</p>
              </Link>
              <Link to="/" className="footer-link">
                <p>Refunds</p>
              </Link>
              <Link to="/" className="footer-link">
                Accessibility
              </Link>
            </Col>
            <Col lg xs={{ span: 12 }}>
              <p className="footer-title">About</p>
              <Link to="/" className="footer-link">
                <p>Our Story</p>
              </Link>
              <Link to="/" className="footer-link">
                <p>Benefits</p>
              </Link>
              <Link to="/" className="footer-link">
                <p>Team</p>
              </Link>
              <Link to="/" className="footer-link">
                Careers
              </Link>
            </Col>
            <Col lg xs={{ span: 12 }}>
              <p className="footer-title">Follow Us</p>
              <Link to="/" className="footer-link">
                <p>
                  <img src={facebook} alt="facebook icon" className="pr-2" />
                  Facebook
                </p>
              </Link>
              <Link to="/" className="footer-link">
                <p>
                  <img src={twitter} alt="twitter icon" className="pr-2" />
                  Twitter
                </p>
              </Link>
              <Link to="/" className="footer-link">
                <p>
                  <img src={instagram} alt="instagram icon" className="pr-2" />
                  Instagram
                </p>
              </Link>
            </Col>
            <Col lg={{ order: 'last' }} xs={{ span: 12, order: 'first' }}>
              <button type="button" onClick={scrollTop}>
                <img src={upchevron} alt="up chevron" />
              </button>
            </Col>
          </Row>
          <Row className="py-5">
            <Col xs={{ span: 12 }} className="pb-2">
              <Link to="/" className="footer-link">
                <span className="pr-5">Terms and Conditions</span>
              </Link>
              <Link to="/" className="footer-link">
                <span>Privacy Policy</span>
              </Link>
            </Col>
            <Col xs={{ span: 12 }} className="copyright">
              Copyright Â© 2020. Game Nation. All rights reserved.
            </Col>
          </Row>
        </Container>
      </footer>
    </>
  );
}
